const preloader=document.querySelector(".preloader");window.addEventListener("load",()=>{preloader.classList.add("hide-preloader")});const main=document.querySelector(".app"),banner=document.querySelector(".banner");window.onload=(()=>{main.classList.add("view-content"),banner.classList.add("swipe-up")});const r=rolly({view:document.querySelector(".app"),native:!0});r.init(),AOS.init({duration:1500});const header=document.querySelector(".header"),btn=document.querySelector(".nav-toggle");btn.addEventListener("click",()=>header.classList.toggle("open")),window.addEventListener("scroll",()=>{window.scrollY>header.getBoundingClientRect().height?header.classList.add("fixed-header"):header.classList.remove("fixed-header")});